mapper.select:
		select faq.`faq_id`,faq.`repository_id`, tr.`name` as repositoryName,`question`,`answer`,faq.`major_id`,tm.`name` as majorName,faq.`creater`,faq.`createtime`,`amount`,`parentId`,tkm.keygroup,tkm.keygroup_name from t_faq faq
			LEFT JOIN t_major tm ON tm.major_id=faq.major_id
			LEFT JOIN t_repository tr on tr.repository_id=faq.repository_id
			left JOIN t_keyword_middle tkm on tkm.faq_id=faq.faq_id      

		  		   and faq_id = #{faqId} 
		  		   and faq.repository_id = #{repositoryId} 
		  		   and question like #{question} 
		  		   and answer = #{answer} 
		  		   and faq.major_id = #{majorId} 
		  		   and faq.creater = #{creater} 
		  		   and faq.createtime = #{createtime} 
		  		   and amount = #{amount} 
		  		   and parentId = #{parentId} 



                order by ${sort} ${order}


                order by faq_id asc



			limit #{offset}, #{limit}

select count(*) from t_faq

