mapper.select:
			select `replace_id`,tkr.`keyword_id`,`charKey`,tk.`name` as keyword from t_keyword_replace tkr left JOIN t_keyword  tk on tk.keyword_id=tkr.keyword_id

		  		   and replace_id = #{replaceId} 
		  		   and keyword_id = #{keywordId} 
		  		   and charKey like #{charkey} 



                order by ${sort} ${order}


                order by replace_id desc



			limit #{offset}, #{limit}

select count(*) from t_keyword_replace

  		   and replace_id = #{replaceId} 
  		   and keyword_id = #{keywordId} 
  		   and charKey like #{charkey}
