mapper.select:
		select faq.`faq_id`,faq.`repository_id`, tr.`name` as repositoryName,`question`,`answer`,faq.`major_id`,tm.`name` as majorName,faq.`creater`,faq.`createtime`,`amount`,`parentId`,
		faq.`flow_identify` as flowIdentify,faq.`is_flow` as isFlow,faq.`flow_level` as flowLevel,
		tkm.keygroup_priority as keygroupPriority,
		tkm.keygroup,tkm.keygroup_name from t_faq faq
			LEFT JOIN t_major tm ON tm.major_id=faq.major_id
			LEFT JOIN t_repository tr ON tr.repository_id=faq.repository_id
			LEFT JOIN t_keyword_middle tkm ON tkm.faq_id=faq.faq_id      

		  		   and faq_id = #{faqId} 
		  		   and faq.repository_id = #{repositoryId} 
		  		   and question like #{question} 
		  		   and answer = #{answer} 
		  		   and faq.major_id = #{majorId} 
		  		   and faq.creater = #{creater} 
		  		   and faq.createtime = #{createtime} 
		  		   and amount = #{amount} 
		  		   and parentId = #{parentId} 
		  		   and faq.flow_identify = #{flowIdentify} 
		  		   and faq.is_flow = #{isFlow} 
		  		   and faq.flow_level = #{flowLevel} 



                order by ${sort} ${order}


                order by faq_id asc



			limit #{offset}, #{limit}

select count(*) from t_faq

  		   and faq_id = #{faqId} 
  		   and repository_id = #{repositoryId} 
  		   and question like #{question} 
  		   and answer = #{answer} 
  		   and major_id = #{majorId} 
  		   and creater = #{creater} 
  		   and createtime = #{createtime} 
  		   and amount = #{amount} 
